insert  into `freecms_channel`(`id`,`name`,`templet`,`contentTemplet`,`img`,`description`,`parId`,`site`,`url`,`state`,`orderNum`,`clickNum`,`navigation`,`pagemark`,`htmlChannel`,`htmlChannelOld`,`htmlParchannel`,`htmlSite`,`indexnum`,`maxpage`) values ('7bdbcb40-0ca7-4bc7-91da-e8cb9c569a2b','解决方案','','','','<h1>\r\n	解决方案</h1>\r\n<h2>\r\n	政府部门网站解决方案</h2>\r\n<p>\r\n	政府部门网站解决方案倡导&quot;安全、共享、协同、应用&quot;的理念，遵循国家电子政务标准实施中&quot;统一组织领导、统一规划实施、统一标准规范、同一网络平台、统一安全管理&quot;的基本原则，充分发挥政府网站有效服务社会的应用职能，有效推进政府信息群架构进程。</p>\r\n<h2>\r\n	集团企业门户解决方案</h2>\r\n<p>\r\n	集团企业门户解决方案从&quot;集成管理、随需应变&quot;的理念出发，不但把集团的企业、集团的产品等相关信息展示给我们的客户、合作伙伴和业内同行，同时也能给他们提供一个便捷有效的反馈平台，商务平台，在网上实现在线企业的概念。使集团的业务能超越时间和空间的限制，在业内更具有竞争力。</p>\r\n<h2>\r\n	大型资讯门户整体解决方案</h2>\r\n<p>\r\n	大型资讯类门户网站最核心的部分是解决资讯来源问题，需要保证能够获取大量实时资讯内容，同时需要保证网站能够无差错不间断的稳定运行。 大型信息门户整体解决方案专为大型资讯类门户网站而设计，定时自动采集模块能够不断获取资讯内容，定时生成、定时备份等多项措施能够减轻维护人员工作量，同时经过考验的CMS系统能够为大型网站提供最佳的整体方案解决可能遇到的各种问题。</p>\r\n<h2>\r\n	大型门户CMS集成解决方案</h2>\r\n<p>\r\n	大型门户网站需要一个强大的CMS系统作为支撑，要求CMS系统不但能够管理资讯信息，同时能够将门户系统内的业务相关数据集成显示，统一纳入CMS中进行管理。 大型门户CMS集成解决方案能够将电子商务门户、行业门户网站、娱乐门户网站、证券门户网站等拥有除资讯外信息的多类网站整合到FreeCMS系统中进行维护管理，将业务数据、产品数据、信息交互等多种信息静态化，使维护大型网站不再困难。</p>\r\n<h2>\r\n	企业信息门户解决方案</h2>\r\n<p>\r\n	针对各种不同的企业，FreeCMS将为您提供全方位的网站解决方案，为您搭建一个集个性化与网下活动同步、拓展范围更广、信息及其应用范围更广的专业网络宣传与交流门户平台。</p>','f9dda97c-a26c-40a3-8a13-12db9538c864','7a4d22d6-762b-46e5-895b-d62da52bf9cb','','1',2,NULL,'1','solution',NULL,NULL,NULL,NULL,92,0);
